function open_func(input, env) {
  console.log('open: ' + input);
  var entry = JSON.parse(sessionStorage.getItem(input));
  console.log(entry);

  if (entry['name']) {
    document.getElementById('ad_username').innerHTML = entry['name'];
    document.getElementById('ad_mail').href = 'mailto:' + entry['email'];
    document.getElementById('ad_mail').innerHTML = entry['email'];
  } else {
    var ele = document.getElementById('c' + input);
    console.log(ele);
    document.getElementById('ad_username').innerHTML = ele.getAttribute('data-name');
    document.getElementById('ad_mail').href = 'mailto:' + ele.getAttribute('data-email');
    document.getElementById('ad_mail').innerHTML = ele.getAttribute('data-email');
  }

  if (entry['admin']) {
    document.getElementById('ad_admin').checked = true;
  }

  if (entry['unternehmensdaten']) {
    document.getElementById('ad_verwaltung1').checked = true;
  }

  if (entry['rechnungen']) {
    document.getElementById('ad_verwaltung2').checked = true;
  }

  if (entry['baustellen']) {
    document.getElementById('ad_verwaltung3').checked = true;
  }

  if (entry['preise']) {
    document.getElementById('ad_mietprozess1').checked = true;
  }

  if (entry['mietdauerverlaengerung']) {
    document.getElementById('ad_mietprozess2').checked = true;
  }

  if (entry['bestellen']) {
    document.getElementById('ad_mietprozess3').checked = true;
  }

  if (entry['auswahl']) {
    document.getElementById('ad_mietprozess4').checked = true;
  }

  if (entry['kategorien']) {
    var kategorien = entry['kategorien']; //console.log(kategorien);

    var fields = document.getElementsByClassName('inputfield');
    var tocheck = new Array(); //console.log(kategorien['count']);

    for (let y = 0; y < kategorien['count']; y++) {
      //console.log(kategorien[y]);
      if (kategorien[y]['value'] == 1) {
        var line1 = kategorien[y]['name'];
        tocheck.push(line1);
      }

      for (let y2 = 0; y2 < kategorien[y]['count']; y2++) {
        //console.log(kategorien[y][y2]);
        if (kategorien[y][y2]['value'] == 1) {
          var line2 = kategorien[y]['name'] + '-+-' + kategorien[y][y2]['name'];
          tocheck.push(line2);
        }

        for (let y3 = 0; y3 < kategorien[y][y2]['count']; y3++) {
          //console.log(kategorien[y][y2][y3]);
          if (kategorien[y][y2][y3]['value'] == 1) {
            var line3 = kategorien[y]['name'] + '-+-' + kategorien[y][y2]['name'] + '-+-' + kategorien[y][y2][y3]['name'];
            tocheck.push(line3);
          }
        }
      }
    }

    for (let x = 0; x < fields.length; x++) {
      //console.log((fields[x] as HTMLInputElement).name);
      var found = false;

      for (let x2 = 0; x2 < tocheck.length; x2++) {
        //console.log('TO Check: ' + tocheck[x2]);
        if (fields[x].name == tocheck[x2]) {
          found = true;
        }
      }

      if (found) {
        fields[x].checked = true;
      } else {
        fields[x].checked = false;
      }
    }
  } //console.log(tocheck);


  document.getElementById('specific').style.display = 'block';
  document.getElementById('generell').style.display = 'none'; //console.log(entry);

  env.calc();
}

