    generateInput2(eingabe) {
      var result = '';

      for (let i = 0; i < eingabe.length; i++) {
        if (result == '') {
          result = '{"' + eingabe[i] + '":"' + eingabe[i + 1] + '"';
        } else {
          result += ',"' + eingabe[i] + '":"' + eingabe[i + 1] + '"';
        }

        i++;
      }

      result += '}';
      return result;
    }

