    slugify(input) {
      input = input.replace(' ', '-');
      input = input.toLowerCase();
      input = input.replace('ä', 'ae');
      input = input.replace('ö', 'oe');
      input = input.replace('ü', 'ue');
      input = input.replace('ß', 'sz');
      input = input.replace('(', '');
      input = input.replace(')', '');
      input = input.replace('.', '');
      input = input.trim();

      if (input.includes('gabel') && input.includes('tele')) {
        input = 'gabel-telestapler';
      }

      return input;
    }

